  constructor(props) {
    super(props);

    this.setState = {
      icon: [
        { text: "หน่วยงานที่เกี่ยวข้อง", pic: "/static/department.svg" },
        { text: "หน่วยงานที่เกี่ยวข้อง", pic: "/static/department.svg" },
        { text: "หน่วยงานที่เกี่ยวข้อง", pic: "/static/department.svg" },
        { text: "หน่วยงานที่เกี่ยวข้อง", pic: "/static/department.svg" }

      ]
    }
  }

  async componentDidMount() {
    localStorage.setItem("myOauth", JSON.stringify(await Sheetapi.postSheetValues()))
  }

  render() {
    return (
      <div className="warp">
        <Nav name="main" />
        <BG />

        <div className="warp-menu" >
