import React from 'react'
import Sheetapi from '../../config/api'
import {
    LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ReferenceLine,
} from 'recharts';

const list = [{}]

const XXX = [
    {
        name: 'Page A', uv: 4000, pv: 2400, amt: 2400,
    },
    {
        name: 'Page B', uv: 3000, pv: 1398, amt: 2210,
    },
    {
        name: 'Page C', uv: 2000, pv: 9800, amt: 2290,
    },
    {
        name: 'Page D', uv: 2780, pv: 3908, amt: 2000,
    },
    {
        name: 'Page E', uv: 1890, pv: 4800, amt: 2181,
    },
    {
        name: 'Page F', uv: 2390, pv: 3800, amt: 2500,
    },
    {
        name: 'Page G', uv: 3490, pv: 4300, amt: 2100,
    },
];

class Chart extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            data: [],
            ar: [],
            isLoad: true
        }
    }

    async componentDidMount() {

        let userOauth = JSON.parse(localStorage.getItem("myOauth"))
        this.access_token = userOauth.data.access_token
        await this.list('!B2:D')
    }

    list = async (value) => {

        try {

            this.list = await Sheetapi.getSheetValues(this.access_token, value)

            for (let i = 0; i < this.list.length; i++) {
                let value = await { name: this.list[i][0], uv: this.list[i][1], pv: this.list[i][2] }
                this.setState(prevState => ({
                    data: [...prevState.data, value]
                }))
            }
            this.state.isLoad = false
            console.log('data', this.state.data)
        } catch (err) {
            console.log(err);
        }

    }

    render() {
        const { data, isLoad } = this.state

        return (
            <div>
                {
                    data ?

                        <div className="warp-chart">
                            {/* <LineChart width={1000} height={500} data={data}>
                                <CartesianGrid strokeDasharray="3 3"/>
                                <XAxis dataKey="name" padding={{ left: 5, right: 5 }} />
                                <YAxis  dataKey="ชาย60ปีขึ้นไป" />
                                <Tooltip />
                                <Legend />
                                <Line type="monotone" dataKey="ชาย60ปีขึ้นไป" stroke="#8884d8" activeDot={{ r: 8 }}/>
                                <Line type="monotone" dataKey="หญิง60ปีขึ้นไป" stroke="#82ca9d" />
                            </LineChart> */}

                            <LineChart width={500} height={300} data={XXX}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="name" padding={{ left: 30, right: 30 }} />
                                <YAxis />
                                <Tooltip />
                                <Legend />
                                <Line type="monotone" dataKey="uv" stroke="#8884d8" activeDot={{ r: 8 }} />
                                <Line type="monotone" dataKey="pv" stroke="#82ca9d" />
                            </LineChart>
                        </div>
                        : null
                }

                

            </div>
        )
    }
}

export default Chart;
