import React from 'react'
import Sheetapi from '../../config/api'
import {
    LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ReferenceLine,
} from 'recharts';

const data = [{}]
class Chart extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            data: [],
            ar: [],
            isLoad: true
        }
    }

    async componentDidMount() {

        let userOauth = JSON.parse(localStorage.getItem("myOauth"))
        this.access_token = userOauth.data.access_token
        await this.list('!B2:D')
    }

    list = async (value) => {

        try {

            this.list = await Sheetapi.getSheetValues(this.access_token, value)

            for (let i = 0; i < this.list.length; i++) {
                let value = await { name: this.list[i][0], ชาย60ปีขึ้นไป: this.list[i][1], หญิง60ปีขึ้นไป: this.list[i][2] }
                this.setState(prevState => ({
                    data: [...prevState.data, value]
                }))
            }
            this.state.isLoad = false
            console.log('data', data)
        } catch (err) {
            console.log(err);
        }

    }

    render() {
        const { data, isLoad } = this.state

        return (
            <div>
                {
                    data ?

                        <div className="warp-chart">
                            <LineChart width={500} height={300} data={data}>
                                <CartesianGrid />
                                {console.log('X', data)}
                                <XAxis dataKey="name" />
                                <YAxis />
                                <Tooltip />
                                <Legend />
                                <Line type="monotone" dataKey="ชาย60ปีขึ้นไป" stroke="#8884d8" />
                                <Line type="monotone" dataKey="หญิง60ปีขึ้นไป" stroke="#82ca9d" />
                            </LineChart>
                        </div>
                        : null
                }

            </div>
        )
    }
}

export default Chart;
