import React from 'react'
import Nav from '../components/nav'
import '../styles/base.scss'
import Sheetapi from '../config/api'
import BG from '../components/layout/bg';

class Home extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      icon: [
        { text: "หน่วยงานที่เกี่ยวข้อง", pic: "/static/department.svg" },
        { text: "หน่วยงานดูแลผู้สูงอายุ", pic: "/static/department.svg" },
        { text: "เอกสารที่จำเป็น", pic: "/static/department.svg" },
        { text: "กิจกรรม", pic: "/static/department.svg" }

      ]
    }
  }

  async componentDidMount() {
    localStorage.setItem("myOauth", JSON.stringify(await Sheetapi.postSheetValues()))
  }

  render() {
    return (
      <div className="warp">
        <Nav name="main" />
        <BG />

        <div className="warp-menu" >
          <div className="menu">
            <div className="icon">
              {
                this.state.icon.map((item, index) => {
                  return (
                    <React.Fragment key={index}>
                      <img src={item.pic} alt="icon" />
                      {/* <h6>{item.text}</h6> */}
                    </React.Fragment>
                  )
                })
              }
              {
                this.state.icon.map((item, index) => {
                  return (
                    <React.Fragment key={index}>
                      {/* <img src={item.pic} alt="icon" /> */}
                      <h6>{item.text}</h6>
                    </React.Fragment>
                  )
                })
              }

            </div>

          </div>

        </div>


        <div className="warp-content" >
          <div className="content"></div>
        </div>

      </div>
    )
  }
}
export default Home
