import React from 'react'
import Nav from '../components/nav'
import '../styles/base.scss'
import Sheetapi from '../config/api'
import BG from '../components/layout/bg';


class Home extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      activeImg: false,
      icon: [
        { text: "หน่วยงานที่เกี่ยวข้อง", pic: "/static/bank.svg",href:"/",name:"icon1" },
        { text: "หน่วยงานดูแลผู้สูงอายุ", pic: "/static/department.svg",href:"/department",name:"icon2"  },
        { text: "เอกสารที่จำเป็น", pic: "/static/contract.svg",href:"/document",name:"icon3"  },
        { text: "กิจกรรม", pic: "/static/active.svg",href:"/active",name:"icon4"  }

      ],
      iconhover: [
        { text: "หน่วยงานที่เกี่ยวข้อง", pic: "/static/bankhover.svg",href:"/",name:"icon1" },
        { text: "หน่วยงานดูแลผู้สูงอายุ", pic: "/static/departmenthover.svg",href:"/department",name:"icon2"  },
        { text: "เอกสารที่จำเป็น", pic: "/static/docmenthover.svg",href:"/document",name:"icon3"  },
        { text: "กิจกรรม", pic: "/static/activehover.svg",href:"/active",name:"icon4"  }

      ]
    }
  }

  async componentDidMount() {
    this.userOauth = JSON.parse(localStorage.getItem("myOauth"))
    localStorage.setItem("myOauth", JSON.stringify(await Sheetapi.postSheetValues()))

  }

  render() {
    return (
      <div className="warp">
        <Nav name="main" />
        <BG />

        <div className="warp-menu" >
          <div className="menu">
            <div className="warp-icon">
              {
                this.state.icon.map((item, index) => {
                  return (
                    <a href={item.href}>
                      <div className="icon" key={index}>
                      <div className="icon-img">
                        <img key={index}  src={item.pic} alt={item.name} />
                        {
                          this.state.iconhover.map((items,indexs) => {
                            return(
                              <img key={indexs}  className="hover" src={items.pic} alt={items.name} />
                            )
                          })
                        }

                      </div>

                      <h6>{item.text}</h6>
                    </div>
                    </a>

                  )
                })
              }
            </div>
          </div>
        </div>

        <div className="warp-content" >
          <div className="content">
          </div>
        </div>

      </div>
    )
  }
}
export default Home
